import './style.css'
import Envelope from "./components/Envelope";
import {useRef, useState} from "react";
import Hearts from "./components/Hearts";

function App(){
    const divRef = useRef(null)
    const [msg, setMsg] = useState("Click envelope to see a nice message...")
    const [showHeart, setShowHeart] = useState(false)

    const handleClick = () => {
        if (divRef.current){
            divRef.current.style.backgroundColor = "lightpink"
        }
        setMsg("Will you be my Valentine?")
        setShowHeart(true)
    }

    return (
        <div id="background" ref={divRef}>
            <div className="message-body">
                {showHeart && <Hearts/>}
                <img src="/images/hearts.gif" alt="hearts"/>
                {msg}
                <div onClick={handleClick}>
                    <Envelope/>
                </div>
            </div>
        </div>
    );
}

export default App;
