import './style.css'
import ClosedEnvelope from "./components/ClosedEnvelope";
import closed from "./images/closedEnv.png";
import openEnv from "./images/openEnv.png";
import {useState} from "react";

function App(){
    const [imageSrc, setImageSrc] = useState(closed);

    function openEnvelope() {
        setImageSrc(openEnv)
    }

    return (
        <div id="background">
            <div className="message-body">
                Tap envelope to see a nice message
                <div>
                    <img id="env" src={imageSrc} alt="envelope" width="400" onClick={openEnvelope}/>
                </div>
            </div>
        </div>
    );
}

export default App;
